import osimport config# Globale variabelen makenlogfolder, logfile = config.init_cfg()if logfolder == "":    path = logfileelse:    path = str(logfolder) + '\\' + logfile# Functie voor het uitlezen van het logbestand en hier de mislukte downloads van te laten ziendef read_log(log_path):    # Try-Except voor de foutafhandeling wanneer een bestand niet bestaat    try:        file_in = open(log_path, 'r')        lines = file_in.readlines()        file_in.close()        for line in lines:            if 'FAIL DOWNLOAD' in line:                print(line)    except FileNotFoundError:        print('FOUT: Bestand of pad bestaat niet!')# Functie voor het maken van het menudef menu():    print('Imteq FTP\n\nMaak een keuze:\n1: Lijst met mislukte downloads\n2: Top 10 gedownloadde bestanden\n0: Afsluiten')    menu_choice = input()    clear()    if menu_choice == '1':        read_log(path)        input('Druk op een toets om terug te gaan naar het menu...')        clear()        menu()    elif menu_choice == '2':        print('Top 10')        input('Druk op een toets om terug te gaan naar het menu...')        clear()        menu()    elif menu_choice == '0':        exit()    else:        print('FOUT: Incorrecte invoer!')        input('Druk op een toets om terug te gaan naar het menu...')        clear()        menu()# Functie voor het legen van de CLIdef clear():    # voor het legen van de CLI wordt hiet een commando uitgevoerd op basis van het OS    osname = os.name    # Windows    if osname == 'nt':        os.system('cls')    # Linux    elif osname == 'posix':        os.system('clear')    # Anti Error    else:        print('\n' * 30)menu()